// src/types/vite.d.ts
declare module '*.noiir?raw' {
  const content: string;
  export default content;
}

declare module '*.png' {
  const src: string;
  export default src;
}

declare module '*Group 26.png' {
  const src: string;
  export default src;
}

declare module '*Group 26.png?url' {
  const src: string;
  export default src;
}

// Vite ?url query parameter
declare module '*?url' {
  const src: string;
  export default src;
}

declare module '*.jpg' {
  const src: string;
  export default src;
}

declare module '*.jpeg' {
  const src: string;
  export default src;
}

declare module '*.gif' {
  const src: string;
  export default src;
}

declare module '*.svg' {
  const src: string;
  export default src;
}

declare module '*.webp' {
  const src: string;
  export default src;
}